con:config.script:
def custname = context.expand( '${CreateCustomer#Response#//*:firstname}' )

// Print the username just so we can debug it
log.info "Customer is $custname"

// If user is not ExpertzMe, the next step to execute should be logout service
if(custname != "Sindu"){
	log.info "Do not Call Customer"
	testRunner.gotoStepByName("GetAllCustomers")
}
else{
// Execute the next step as normal
log.info "Call the Customer"
}

import com.eviware.soapui.model.testsuite.TestRunner.Status

// get TestCase
def tc = testRunner.testCase.testSuite.project.testSuites["Create Call and Delete Customer"].testCases["Create Call and Delete Customer with Script"]
log.info tc

//set credentials properties from the script itself
tc.testSteps["Customer Details"].setPropertyValue("email", "sindhum27@gmail.com" )
tc.testSteps["Customer Details"].setPropertyValue("firstname", "Sindhu" )
tc.testSteps["Customer Details"].setPropertyValue("lastname", "Marar" )

//Run test synchronously
def runner = tc.run( null, false )

// show the test case result status
log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
log.info "Number of test steps which ran: " + runner.results.size()

// assert that it didn't fail
assert runner.status != Status.FAILED : runner.reason

// assert that only five steps ran!!
assert runner.results.size() == 5

import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
def filepath = "C:/Users/SindhuMarar/Documents/Sindhu/SRM/SDET_Learning/API/CustData.xls"
File Excel = new File(filepath)


Workbook wb = Workbook.getWorkbook(Excel)

Sheet sh = wb.getSheet("Sheet1")

def rowsize = sh.getRows().toInteger();
def columnsize = sh.getColumns().toInteger();

def email,firstname,lastname;

log.info "$rowsize and $columnsize"


for(i=0;i<=rowsize;i++)
{
	if(i==0)
	{
		email = sh.getCell(0,i).getContents()
		log.info email
	}
	else if(i==1)
	{
		firstname = sh.getCell(0,i).getContents()
		log.info firstname
	}
	else if(i==2)
	{
		lastname = sh.getCell(0,i).getContents()
		log.info lastname
	}


}
	// get TestCase
	def tc = testRunner.testCase.testSuite.project.testSuites["Create Call and Delete Customer"].testCases["Create Call and Delete Customer with Script"]
	log.info tc

	//set credentials properties from the script itself
	tc.testSteps["Customer Details"].setPropertyValue("email", email )
	tc.testSteps["Customer Details"].setPropertyValue("firstname", firstname )
	tc.testSteps["Customer Details"].setPropertyValue("lastname", lastname )

	//Run test synchronously
	def runner = tc.run( null, false )

	// show the test case result status
	log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
	log.info "Number of test steps which ran: " + runner.results.size()

	// assert that it didn't fail
	assert runner.status != Status.FAILED : runner.reason

	// assert that only five steps ran!!
	assert runner.results.size() == 5

import groovy.sql.Sql
import com.eviware.soapui.model.testsuite.TestRunner.Status

//Connect to database
def url = 'jdbc:mysql://localhost:3306/api_project';
def user = 'root'
def password = 'root'
def driver = 'com.mysql.jdbc.Driver'

//Register to database
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)

//Connect to sql
def sql = Sql.newInstance(url,user,password,driver)

sql.eachRow("select * from cust_data")
{
	row->
	def email = "$row.Email";
	log.info email

	def firstname = "$row.FirstName"
	log.info firstname

	def lastname = "$row.LastName"
	log.info lastname

	// get TestCase
	def tc = testRunner.testCase.testSuite.project.testSuites["Create Call and Delete Customer"].testCases["Create Call and Delete Customer with Script"]
	log.info tc

	//set credentials properties from the script itself
	tc.testSteps["Customer Details"].setPropertyValue("email", email )
	tc.testSteps["Customer Details"].setPropertyValue("firstname", firstname )
	tc.testSteps["Customer Details"].setPropertyValue("lastname", lastname )

	//Run test synchronously
	def runner = tc.run( null, false )

	// show the test case result status
	log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
	log.info "Number of test steps which ran: " + runner.results.size()

	// assert that it didn't fail
	assert runner.status != Status.FAILED : runner.reason

	// assert that only five steps ran!!
	assert runner.results.size() == 5
}
